import styled from "styled-components/native";

export const Container = styled.View`
  flex: 1;
  background-color: ${({theme}) => theme.colors.background};
`;

export const ImgBackground = styled.ImageBackground`
  flex: 1;
  width: 100%;
  margin-left: auto;
  resizeMode: cover;
`;
