import React from 'react';
import {TouchableWithoutFeedback, Keyboard} from 'react-native';
import { ProgressBar } from 'react-native-paper';
import { View } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome'

import { Container, LogoImg, Title, Text, ContainerHeader, ContainerForm } from './styles';
import LogoFlip from '../../../../assets/logo_flip_cor.png';
import {Input} from '../../../components/SignUp/Input';
import { Button } from '../../../components/SignUp/Button';


export function SignUpSelfie() {
  return (
    <Container>
      <ProgressBar progress={0.28} style={{marginTop: 74, width: "80%", left: "10%", marginBottom: 25, borderRadius: 25}} color="#16B4EE"/>
      <ContainerHeader>
      <Icon name="arrowleft" size={16} color="#4d4d4d" style={{}}/>
        <LogoImg source={LogoFlip}/>
      </ContainerHeader>
      
      <ContainerForm>
      <Title>Hora da Selfie!</Title>
      <Text>Envie uma selfie com boa iluminação e que mostre bem o seu rosto. Evite qualquer tipo de cobertura facial.</Text>
        <TouchableWithoutFeedback onPress={ Keyboard.dismiss} accessible={false}>
          <View>
            <Input inputMode='numeric' placeholderTextColor="rgba(77, 77, 77, 0.7)" placeholder='Senha' style={{marginBottom: 350}}/>

            <Icon name="user-circle" size={16} color="#4d4d4d" style={{}}/>
            <Button title='Tirar foto'/>
          </View>
        </TouchableWithoutFeedback>
    </ContainerForm>
    </Container>
  );
}
